<?php/** * Created by PhpStorm. * User: Win10 * Date: 05.05.2018 * Time: 23:47 */namespace AppBundle\Admin;use Sonata\AdminBundle\Admin\AbstractAdmin;use Sonata\AdminBundle\Datagrid\DatagridMapper;use Sonata\AdminBundle\Datagrid\ListMapper;use Sonata\AdminBundle\Form\FormMapper;use Symfony\Component\Form\Extension\Core\Type\TextareaType;use Symfony\Component\Form\Extension\Core\Type\TextType;class ArticlesAdmin extends AbstractAdmin{    protected function configureFormFields(FormMapper $formMapper){        $formMapper          ->tab('page.with.tab', [            'label' => 'page.parametres'          ])          ->with('page.with', [            'label' => 'page.parametres'          ])            ->add('namePage', TextType::class, [              'label' => $this->trans('name.page')            ])            ->add('title', TextType::class, [              'label' => $this->trans('title')            ])            ->add('articleHtml',TextareaType::class,[                'label' => $this->trans('HTML Format')            ])            ->add('url', TextType::class, [              'label' => $this->trans('url')            ])            ->add('idArticle', TextType::class, [              'label' => $this->trans('article')            ])          ->end()          ->end()        ;    }    protected function configureDatagridFilters(DatagridMapper $datagridMapper)    {        $datagridMapper            ->add('namePage')            ->add('title')            ->add('idArticle')        ;    }    protected function configureListFields(ListMapper $listMapper)    {        $listMapper            ->addIdentifier('title')            ->add('namePage')            ->add('idArticle')        ;    }}