<?phpnamespace AppBundle\Entity;use Doctrine\ORM\Mapping as ORM;/** * CpVille * * @ORM\Table(name="code_postal") * @ORM\Entity(repositoryClass="AppBundle\Repository\CpVilleRepository") */class CpVille{    /**     * @var int     *     * @ORM\Column(name="id", type="integer")     * @ORM\Id     * @ORM\GeneratedValue(strategy="AUTO")     */    private $id;    /**     * @ORM\OneToMany(targetEntity="Societe", mappedBy="cpville")     */    private $societe_cpville;    /**     * @var string     *     * @ORM\Column(name="cp", type="string", length=10)     */    private $cp;    /**     * @var string     *     * @ORM\Column(name="ville", type="string", length=100)     */    private $ville;    /**     * Get id     *     * @return int     */    public function getId()    {        return $this->id;    }    /**     * Set cp     *     * @param string $cp     *     * @return CpVille     */    public function setCp($cp)    {        $this->cp = $cp;        return $this;    }    /**     * Get cp     *     * @return string     */    public function getCp()    {        return $this->cp;    }    /**     * Set ville     *     * @param string $ville     *     * @return CpVille     */    public function setVille($ville)    {        $this->ville = $ville;        return $this;    }    /**     * Get ville     *     * @return string     */    public function getVille()    {        return $this->ville;    }    /**     * @return mixed     */    public function getSocieteCpville()    {        return $this->societe_cpville;    }    /**     * @param mixed $societe_cpville     */    public function setSocieteCpville($societe_cpville)    {        $this->societe_cpville = $societe_cpville;    }    /**     * @return string     */    public function __toString()    {         return (string)$this->ville;    }}